def get_frequencies_with_percentage_array(text):
    char_frequencies = {}
    for index, char in enumerate(text):
        #char = unichr(ord(char) - 65)
        char = ord(char) - 65
        if char in char_frequencies:
            char_frequencies[char] += 1
        else:
            char_frequencies[char] = 1

    freq_with_percent = {}
    for key in char_frequencies:
        val = char_frequencies[key]
        freq_with_percent[key] = {val: '%0.2f%%' % (float(val) / float(len(part_one)) * 100)}

